from typing import Any, List, Optional
from .base_chunker import BaseChunker
from chunking_evaluation.utils import Language
from langchain_google_genai import GoogleGenerativeAIEmbeddings
import re
import numpy as np
import logging

# Set up logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)

def _split_text_with_regex(
    text: str, separator: str, keep_separator: bool
) -> List[str]:
    # Now that we have the separator, split the text
    if separator:
        if keep_separator:
            # The parentheses in the pattern keep the delimiters in the result.
            _splits = re.split(f"({separator})", text)
            splits = [_splits[i] + _splits[i + 1] for i in range(1, len(_splits), 2)]
            if len(_splits) % 2 == 0:
                splits += _splits[-1:]
            splits = [_splits[0]] + splits
        else:
            splits = re.split(separator, text)
    else:
        splits = list(text)
    return [s for s in splits if s != ""]

class RecursiveTokenChunker(BaseChunker):
    """Splitting text by recursively looking at characters.

    Recursively tries to split by different characters to find one
    that works, while ensuring semantic coherence using embeddings.
    """

    def __init__(
        self,
        chunk_size: int = 4000,
        chunk_overlap: int = 200,
        separators: Optional[List[str]] = None,
        keep_separator: bool = True,
        is_separator_regex: bool = False,
        avg_chunk_size: int = 400,
        min_chunk_size: int = 50,
        **kwargs: Any,
    ) -> None:
        """Create a new RecursiveTokenChunker."""
        super().__init__(chunk_size=chunk_size, chunk_overlap=chunk_overlap, keep_separator=keep_separator, **kwargs)
        self._separators = separators or ["\n\n", "\n", ".", "?", "!", " ", ""]
        self._is_separator_regex = is_separator_regex
        self.avg_chunk_size = avg_chunk_size
        self.min_chunk_size = min_chunk_size
        self.embedding_model = GoogleGenerativeAIEmbeddings(model="models/embedding-001")

    def _split_text(self, text: str, separators: List[str]) -> List[str]:
        """Split incoming text and return chunks."""
        final_chunks = []
        # Get appropriate separator to use
        separator = separators[-1]
        new_separators = []
        for i, _s in enumerate(separators):
            _separator = _s if self._is_separator_regex else re.escape(_s)
            if _s == "":
                separator = _s
                break
            if re.search(_separator, text):
                separator = _s
                new_separators = separators[i + 1 :]
                break

        _separator = separator if self._is_separator_regex else re.escape(separator)
        splits = _split_text_with_regex(text, _separator, self._keep_separator)

        # Now go merging things, recursively splitting longer texts.
        _good_splits = []
        _separator = "" if self._keep_separator else separator
        for s in splits:
            if self._length_function(s) < self._chunk_size:
                _good_splits.append(s)
            else:
                if _good_splits:
                    merged_text = self._merge_splits(_good_splits, _separator)
                    final_chunks.extend(merged_text)
                    _good_splits = []
                if not new_separators:
                    final_chunks.append(s)
                else:
                    other_info = self._split_text(s, new_separators)
                    final_chunks.extend(other_info)
        if _good_splits:
            merged_text = self._merge_splits(_good_splits, _separator)
            final_chunks.extend(merged_text)
        return final_chunks

    def _merge_splits(self, splits: List[str], separator: str) -> List[str]:
        """Merge splits while ensuring semantic coherence."""
        merged_chunks = []
        current_chunk = ""
        for split in splits:
            if len(current_chunk) + len(split) <= self._chunk_size:
                current_chunk += split + separator
            else:
                merged_chunks.append(current_chunk.strip())
                current_chunk = split + separator
        if current_chunk:
            merged_chunks.append(current_chunk.strip())
        return merged_chunks

    def split_text(self, text: str) -> List[str]:
        """Split text into chunks while ensuring semantic coherence."""
        initial_chunks = self._split_text(text, self._separators)
        embeddings = self._generate_embeddings(initial_chunks)
        distances = self._calculate_cosine_distances(embeddings)
        return self._finalize_chunks(initial_chunks, distances)

    def _generate_embeddings(self, chunks: List[str]) -> List[np.ndarray]:
        """Generate embeddings for the given text chunks."""
        try:
            return self.embedding_model.embed_documents(chunks)
        except Exception as e:
            logger.error(f"Error generating embeddings: {e}")
            return []

    def _calculate_cosine_distances(self, embeddings: List[np.ndarray]) -> List[float]:
        """Calculate cosine similarity distances between consecutive embeddings."""
        distances = []
        for i in range(len(embeddings) - 1):
            similarity = np.dot(embeddings[i], embeddings[i + 1]) / (np.linalg.norm(embeddings[i]) * np.linalg.norm(embeddings[i + 1]))
            distances.append(1 - similarity)  # Cosine distance
        return distances

    def _finalize_chunks(self, chunks: List[str], distances: List[float]) -> List[str]:
        """Create final chunks based on semantic shifts."""
        threshold = np.percentile(distances, 80)  # Use dynamic threshold
        split_points = [i for i, dist in enumerate(distances) if dist > threshold]
        logger.info(f"Identified {len(split_points)} semantic split points.")

        thematic_chunks = []
        current_chunk = ""
        for i, chunk in enumerate(chunks):
            current_chunk += chunk + " "
            if i in split_points:
                thematic_chunks.append(current_chunk.strip())
                current_chunk = ""
        if current_chunk:
            thematic_chunks.append(current_chunk.strip())

        logger.info(f"Generated {len(thematic_chunks)} final thematic chunks.")
        return thematic_chunks

